import { useState } from "react";
import { v4 as uuidv4 } from "uuid";

import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";

const Signup = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [birthdate, setBirthdate] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [contact, setContact] = useState("");
  const [nacionality, setNacionality] = useState("");
  const [nacionalityDropdownTitle, setNacionalityDropdownTitle] = useState(
    "Select a nacionality"
  );
  const [nacionalities] = useState([
    "Portuguese, English, French, German, Spanish",
  ]);

  const handleClose = () => {
    setShow(false);
    setUsername("");
    setPassword("");
    setBirthdate("");
    setName("");
    setEmail("");
    setAddress("");
    setContact("");
    setNacionality("");
    setNacionalityDropdownTitle("Select a nacionality");
  };
  const handleShow = () => setShow(true);

  const createDonor = () => {
    if (
      !username ||
      !password ||
      !name ||
      !birthdate ||
      !email ||
      !address ||
      !contact ||
      !nacionality
    ) {
      alert("Empty fields!");
      return;
    }

    const newDonor = {
      id: uuidv4(),
      oldId: uuidv4(),
      username: username,
      password: password,
      name: name,
      birthdate: birthdate,
      email: email,
      address: address,
      contact: contact,
      nacionality: nacionality,
    };
    console.log(newDonor);
    handleClose();
  };

  return (
    <>
      <Form>
        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Username
          </Form.Label>
          <Col sm="10">
            <Form.Control
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Password
          </Form.Label>
          <Col sm="10">
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            name
          </Form.Label>
          <Col sm="10">
            <Form.Control
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Email
          </Form.Label>
          <Col sm="10">
            <Form.Control
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Address
          </Form.Label>
          <Col sm="10">
            <Form.Control
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Contact
          </Form.Label>
          <Col sm="10">
            <Form.Control
              type="number"
              value={contact}
              onChange={(e) => setContact(e.target.value)}
            />
          </Col>
        </Form.Group>

        <DropdownButton title={nacionalityDropdownTitle}>
          {nacionalities.map((nac) => (
            <Dropdown.Item
              key={nac}
              as="button"
              onClick={(e) => {
                e.preventDefault();
                setNacionalityDropdownTitle(nac);
                setNacionality(nac);
              }}
            >
              {nac}
            </Dropdown.Item>
          ))}
        </DropdownButton>
      </Form>

      <Button variant="secondary" onClick={handleClose}>
        Close
      </Button>
      <Button variant="primary" onClick={() => createDonor()}>
        Create
      </Button>
    </>
  );
};

export default Signup;
