import { useState, useEffect } from "react";
import axios from "axios";
import { v4 as uuidv4 } from "uuid";

import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const Order = ({ order }) => {
  const [event, setEvent] = useState([]);
  const [show, setShow] = useState(false);

  //Invoice Modal
  const [amount, setAmount] = useState("");
  const [date, setDate] = useState(Date());

  useEffect(() => {
    const getEventById = async () => {
      const event = await fetchEventById();
      setEvent(event);
    };
    getEventById();
  }, []);

  const fetchEventById = async () => {
    const eventId = { eventId: order.eventId };
    const response = await axios.post(
      "http://localhost:8080/get_EVENT_by_Id",
      eventId
    );
    return response.data;
  };

  const handleClose = () => {
    setShow(false);
    setAmount("");
    setDate(new Date());
  };
  const handleShow = () => setShow(true);

  const createInvoice = async (order) => {
    if (!amount || !date) {
      alert("Empty fields!");
      return;
    }

    const newInvoice = {
      id: uuidv4(),
      // oldId: event.id,
      description: order.description,
      date:
        new Date(date).getDate() +
        "/" +
        (new Date(date).getMonth() + 1) +
        "/" +
        new Date(date).getFullYear(),
      misId: order.misId,
      supplCoId: order.supplCoId,
      eventId: order.eventId,
    };
    try {
      await axios.post("http://localhost:8080/create_INVOICE", newInvoice);
      handleClose();
    } catch (e) {
      console.log(e);
    }
  };
  console.log(order);
  return (
    <div className="supplco_invoiceDetails">
      <div className="invoice">
        <Card>
          {event[0] ? (
            <Card.Header className="cardTitle">{event[0].name}</Card.Header>
          ) : (
            ""
          )}
          <Card.Body>
            <Card.Text>{order.description}</Card.Text>
            <Card.Text>{order.date}</Card.Text>
          </Card.Body>

          <Modal
            show={show}
            onHide={handleClose}
            backdrop="static"
            keyboard={false}
          >
            <Modal.Header closeButton>
              <Modal.Title>{event.name} - Invoice</Modal.Title>
            </Modal.Header>

            <Modal.Body>
              <Form>
                <Form.Group as={Row} style={{ marginTop: "10px" }}>
                  <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                    Order Description
                  </Form.Label>
                  <Col sm="10">
                    <Form.Control
                      as="textarea"
                      readOnly
                      value={order.description}
                    />
                  </Col>
                </Form.Group>

                <Form.Group as={Row} style={{ marginTop: "10px" }}>
                  <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                    Order Date
                  </Form.Label>
                  <Col sm="10">
                    <Form.Control type="text" readOnly value={order.date} />
                  </Col>
                </Form.Group>

                <Form.Group as={Row} style={{ marginTop: "10px" }}>
                  <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                    Invoice Amount
                  </Form.Label>
                  <Col sm="10">
                    <Form.Control
                      type="number"
                      value={amount}
                      onChange={(e) => setAmount(e.target.value)}
                    />
                  </Col>
                </Form.Group>

                <Form.Group as={Row} style={{ marginTop: "10px" }}>
                  <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                    Invoice Date
                  </Form.Label>
                  <Col sm="10">
                    <Form.Control type="text" readOnly value={date} />
                  </Col>
                </Form.Group>
              </Form>
            </Modal.Body>

            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
              </Button>
              <Button variant="secondary" onClick={() => createInvoice(order)}>
                Create Invoice
              </Button>
            </Modal.Footer>
          </Modal>

          <Button
            variant="btn btn-dark btn-sm"
            onClick={() => handleShow()}
            style={{ float: "right", marginTop: "20px" }}
          >
            Create Invoice
          </Button>
        </Card>
      </div>
    </div>
  );
};

export default Order;
