import { useState } from "react";
import axios from "axios";
import { uuid } from "uuidv4";

import Donate from "../Donations/Donate";

import "./Events.css";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const EventDetails = ({ event, component, onDonateSuccess }) => {
  const [name, setName] = useState(event.name);
  const [description, setDescription] = useState(event.description);
  const [targetReason, setTargetReason] = useState(event.targetReason);
  const [targetAmount, setTargetAmount] = useState(event.targetAmount);
  const [endDate, setEndDate] = useState(Date(event.endDate));

  const [allowEdit, setAllowEdit] = useState(true);

  const onEditClick = () => {
    setAllowEdit(!allowEdit);
  };

  const onSaveClick = async () => {
    if (
      !name ||
      !description ||
      !targetReason ||
      !targetAmount ||
      !event.beginDate ||
      !endDate
    ) {
      alert("Empty fields!");
      return;
    }

    const newEvent = {
      oldId: uuid(),
      name: name,
      description: description,
      targetReason: targetReason,
      targetAmount: targetAmount,
      beginDate: event.beginDate,
      endDate:
        endDate.getDate() +
        "/" +
        (endDate.getMonth() + 1) +
        "/" +
        endDate.getFullYear(),
      misId: "af29b8c4-21d7-4989-83ce-0c8e9e07af9d",
      solInstId: "9e3b7028-73f0-42b0-a1e0-abcd7d848322",
    };

    try {
      await axios.post("http://localhost:8080/create_EVENT", newEvent);
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <div
      className={`${
        component === "Event.js" ? "eventDetails" : "solInst_eventDetails"
      }`}
    >
      <Form>
        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Title
          </Form.Label>
          <Col sm="10">
            <Form.Control
              plaintext
              readOnly={allowEdit}
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Description
          </Form.Label>
          <Col sm="10">
            <Form.Control
              as="textarea"
              plaintext
              readOnly={allowEdit}
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Target Reason
            </Form.Label>
            <Col sm="10">
              <Form.Control
                plaintext
                readOnly={allowEdit}
                value={targetReason}
                onChange={(e) => setTargetReason(e.target.value)}
              />
            </Col>
          </Form.Group>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Target Amount
            </Form.Label>
            <Col sm="10">
              <Form.Control
                plaintext
                readOnly={targetAmount}
                value={targetAmount}
                onChange={(e) => setTargetAmount(e.target.value)}
              />
            </Col>
          </Form.Group>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Begin Date
            </Form.Label>
            <Col sm="10">
              <Form.Control plaintext readOnly value={event.beginDate} />
            </Col>
          </Form.Group>

          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              End Date
            </Form.Label>
            <Col sm="10">
              {component === "Event.js" ? (
                <Form.Control plaintext readOnly value={event.endDate} />
              ) : (
                <DatePicker
                  selected={new Date(endDate)}
                  readOnly={allowEdit}
                  todayButton="Today"
                  dateFormat="dd/MM/yyyy"
                  onChange={(date) => setEndDate(date)}
                />
              )}
            </Col>
          </Form.Group>
        </Form.Group>
      </Form>

      {component === "MisEvents.js" ? (
        allowEdit ? (
          <Button
            variant="dark"
            size="sm"
            onClick={() => onEditClick()}
            style={{
              marginTop: "20px",
              width: "fit-content",
              alignSelf: "center",
            }}
          >
            Edit
          </Button>
        ) : (
          <>
            <Button
              variant="dark"
              size="sm"
              onClick={() => onEditClick()}
              style={{
                marginTop: "20px",
                width: "fit-content",
                alignSelf: "center",
              }}
            >
              Close
            </Button>

            <Button
              variant="dark"
              size="sm"
              onClick={() => onSaveClick()}
              style={{
                marginTop: "20px",
                width: "fit-content",
                alignSelf: "center",
              }}
            >
              Save
            </Button>
          </>
        )
      ) : (
        <Donate event={event} onDonateSuccess={onDonateSuccess} />
      )}
    </div>
  );
};

export default EventDetails;
