import { useState } from "react";

import ListGroup from "react-bootstrap/ListGroup";
import Button from "react-bootstrap/Button";

const MisList = ({ mis, solInsts }) => {
  const [show, setshow] = useState(false);
  const disableMis = () => {
    setshow(!show);
  };

  const enableMis = () => {
    setshow(!show);
  };

  return (
    <>
      <ListGroup as="ul">
        {mis.length === 0
          ? "No"
          : mis.map((mis) =>
              mis.isEnabled === 1 ? (
                <>
                  <ListGroup.Item as="li" key={mis.id}>
                    Name: {mis.name} <br />
                    Solidarity Institution:
                    {solInsts.map((inst) =>
                      inst.id === mis.solInstId ? inst.name : ""
                    )}
                  </ListGroup.Item>
                  <Button variant="primary" onClick={() => disableMis()}>
                    Disable
                  </Button>
                </>
              ) : (
                <>
                  <ListGroup.Item as="li" key={mis.id} disabled>
                    Name: {mis.name} <br />
                    Solidarity Institution:
                    {solInsts.map((inst) =>
                      inst.id === mis.solInstId ? inst.name : ""
                    )}
                  </ListGroup.Item>
                  <Button variant="primary" onClick={() => enableMis()}>
                    Enable
                  </Button>
                </>
              )
            )}
      </ListGroup>
    </>
  );
};

export default MisList;
