import { useState } from "react";

import Donate from "../Donations/Donate";

import "./Events.css";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const EventDetails = ({ event, component, onDonateSuccess }) => {
  const [name, setName] = useState(event.name);
  const [description, setDescription] = useState(event.description);
  const [targetReason, setTargetReason] = useState(event.targetReason);
  const [targetAmount, setTargetAmount] = useState(event.targetAmount);
  const [beginDate, setBeginDate] = useState(event.beginDate);
  const [endDate, setEndDate] = useState(event.endDate);

  const [readOnly, setReadOnly] = useState(true);

  const onEditClick = () => {
    setReadOnly(!readOnly);
  };

  return (
    <div
      className={`${
        component === "Event.js" ? "eventDetails" : "solInst_eventDetails"
      }`}
    >
      <Form>
        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Title
          </Form.Label>
          <Col sm="10">
            <Form.Control
              plaintext
              readOnly={readOnly}
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Description
          </Form.Label>
          <Col sm="10">
            <Form.Control
              as="textarea"
              plaintext
              readOnly={readOnly}
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Target Reason
            </Form.Label>
            <Col sm="10">
              <Form.Control
                plaintext
                readOnly={readOnly}
                value={targetReason}
                onChange={(e) => setTargetReason(e.target.value)}
              />
            </Col>
          </Form.Group>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Target Amount
            </Form.Label>
            <Col sm="10">
              <Form.Control
                plaintext
                readOnly={targetAmount}
                value={targetAmount}
                onChange={(e) => setTargetAmount(e.target.value)}
              />
            </Col>
          </Form.Group>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Begin Date
            </Form.Label>
            <Col sm="10">
              <Form.Control
                plaintext
                readOnly={readOnly}
                value={event.beginDate}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              End Date
            </Form.Label>
            <Col sm="10">
              <DatePicker
                selected={endDate}
                todayButton="Today"
                dateFormat="dd/MM/yyyy"
                onChange={(date) => {
                  const endDate =
                    date.getDate() +
                    "/" +
                    (date.getMonth() + 1) +
                    "/" +
                    date.getFullYear();
                  setEndDate(endDate);
                }}
              />

              {/* <Form.Control plaintext readOnly={readOnly} value={endDate}
                onChange={(e) => } /> */}
            </Col>
          </Form.Group>
        </Form.Group>
      </Form>

      {component === "MisEvents.js" ? (
        <Button
          variant="dark"
          size="sm"
          onClick={() => onEditClick()}
          style={{
            marginTop: "20px",
            width: "fit-content",
            alignSelf: "center",
          }}
        >
          Edit
        </Button>
      ) : (
        <Donate event={event} onDonateSuccess={onDonateSuccess} />
      )}
    </div>
  );
};

export default EventDetails;
