import { useState } from "react";
import axios from "axios";

import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";

const ProfileDetails = ({ user }) => {
  const [username, setUsername] = useState(user.username);
  const [password, setPassword] = useState(user.password);
  // const [username, setUsername] = useState("");
  // const [username, setUsername] = useState("");
  // const [username, setUsername] = useState("");
  // const [username, setUsername] = useState("");
  // const [username, setUsername] = useState("");
  // const [username, setUsername] = useState("");
  const [allowEdit, setAllowEdit] = useState(true);

  // const onSaveClick = async () => {
  //   if (
  //     !name ||
  //     !description ||
  //     !targetReason ||
  //     !targetAmount ||
  //     !event.beginDate ||
  //     !endDate
  //   ) {
  //     alert("Empty fields!");
  //     return;
  //   }

  //   const newProfile = {
  //     oldId: event.id,
  //     name: name,
  //     description: description,
  //     targetReason: targetReason,
  //     targetAmount: targetAmount,
  //     currentAmount: event.currentAmount,
  //     beginDate: event.beginDate,
  //     endDate:
  //       new Date(endDate).getDate() +
  //       "/" +
  //       (new Date(endDate).getMonth() + 1) +
  //       "/" +
  //       new Date(endDate).getFullYear(),
  //     misId: "1a755c26-5266-496a-a8c6-59d2857e84e7",
  //     solInstId: "479faaff-b3e0-4029-b58d-26d54fa72b59",
  //   };

  //   try {
  //     await axios.post("http://localhost:8080/create_EVENT", newEvent);
  //   } catch (e) {
  //     console.log(e);
  //   }
  // };

  return (
    <>
      <Form>
        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Username
          </Form.Label>
          <Col sm="10">
            <Form.Control
              readOnly={allowEdit}
              value={username}
              onClick={(e) => setUsername(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Password
          </Form.Label>
          <Col sm="10">
            <Form.Control
              readOnly={allowEdit}
              value={password}
              onClick={(e) => setUsername(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Name
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext readOnly={allowEdit} value={user.name} />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Institution
          </Form.Label>
          <Col sm="10">
            <Form.Control
              plaintext
              readOnly={allowEdit}
              value={user.institution}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Address
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext readOnly={allowEdit} value={user.address} />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Email
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext readOnly={allowEdit} value={user.email} />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Contact
          </Form.Label>
          <Col sm="10">
            <Form.Control plaintext readOnly={allowEdit} value={user.contact} />
          </Col>
        </Form.Group>
      </Form>

      <Button
        variant="dark"
        size="sm"
        onClick={() => setAllowEdit(!allowEdit)}
        style={{
          marginTop: "20px",
          width: "fit-content",
          alignSelf: "center",
        }}
      >
        Edit
      </Button>
    </>
  );
};

export default ProfileDetails;
