import { useState } from "react";
import { Link } from "react-router-dom";

import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { registerLocale, setDefaultLocale, DatePicker } from "react-datepicker";
import pt from "date-fns/locale/pt";
import "react-datepicker/dist/react-datepicker.css";

const AddEvent = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [targetReason, setTargetReason] = useState("");
  const [targetAmount, setTargetAmount] = useState(0.0);
  const [beginDate, setBeginDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [solInstId, setSolInstId] = useState("");

  const addEvent = () => {};
  registerLocale("pt", pt);
  return (
    <div className="addEvent_page">
      <Form>
        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Title
          </Form.Label>
          <Col sm="10">
            <Form.Control value={name} />
          </Col>
        </Form.Group>

        <Form.Group as={Row} style={{ marginTop: "10px" }}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Description
          </Form.Label>
          <Col sm="10">
            <Form.Control as="textarea" value={description} />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Target Reason
            </Form.Label>
            <Col sm="10">
              <Form.Control value={targetReason} />
            </Col>
          </Form.Group>

          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Target Amount
            </Form.Label>
            <Col sm="10">
              <Form.Control value={targetAmount + "€"} />
            </Col>
          </Form.Group>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Begin Date
            </Form.Label>
            <DatePicker
              selected={beginDate}
              onChange={(date) => setBeginDate(date)}
              selectsStart
              startDate={beginDate}
              endDate={endDate}
            />
          </Form.Group>

          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              End Date
            </Form.Label>
            <Col sm="10">
              <DatePicker
                selected={endDate}
                onChange={(date) => setEndDate(date)}
                selectsEnd
                startDate={beginDate}
                endDate={endDate}
                minDate={beginDate}
              />
            </Col>
          </Form.Group>
        </Form.Group>
      </Form>

      <Link to="/mis/events" className="btn btn-primary">
        Cancel
      </Link>
      <Link to="/mis/events" className="btn btn-primary" onClick={addEvent}>
        Add
      </Link>
    </div>
  );
};

export default AddEvent;
