import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";
import axios from "axios";

import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import InputGroup from "react-bootstrap/InputGroup";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import DropdownButton from "react-bootstrap/DropdownButton";
import Dropdown from "react-bootstrap/Dropdown";

const AddEvent = ({ onAddEvent }) => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [targetReason, setTargetReason] = useState("");
  const [targetAmount, setTargetAmount] = useState(0.0);
  const [beginDate, setBeginDate] = useState(new Date());
  const [endDate, setEndDate] = useState(new Date());
  const [benefs, setBenefs] = useState([]);
  const [benefId, setBenefId] = useState("");
  const [benefDropdownTitle, setBenefDropdownTitle] = useState(
    "Select a Beneficiary"
  );

  const [show, setShow] = useState(false);

  const [users, setUsers] = useState([]);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [nameBenef, setNameBenef] = useState("");
  const [birthdate, setBirthdate] = useState(new Date());
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [contact, setContact] = useState("");
  const [nacionality, setNacionality] = useState("");
  const [nacionalityDropdownTitle, setNacionalityDropdownTitle] = useState(
    "Select a nacionality"
  );
  const [nacionalities] = useState([
    "Portuguese",
    "English",
    "French",
    "German",
    "Spanish",
  ]);

  useEffect(() => {
    const getBenefs = async () => {
      const benefs = await fetchBenefs();
      setBenefs(benefs);
    };
    getBenefs();

    const getUsers = async () => {
      const users = await fetchUsers();
      setUsers(users);
    };
    getUsers();
  }, []);

  const fetchBenefs = async () => {
    const response = await axios.get("http://localhost:8080/get_all_BENEFS");
    return response.data;
  };

  const fetchUsers = async () => {
    const response = await axios.get("http://localhost:8080/get_all_Users");

    return response.data;
  };

  const addEvent = () => {
    if (
      !name ||
      !description ||
      !targetReason ||
      !targetAmount ||
      !beginDate ||
      !endDate ||
      !benefId
    ) {
      alert("Empty fields!");
      return;
    }

    onAddEvent({
      name,
      description,
      targetReason,
      targetAmount,
      beginDate,
      endDate,
      benefId,
    });

    setName("");
    setDescription("");
    setTargetReason("");
    setTargetAmount("");
    setBeginDate("");
    setEndDate("");
    setBenefId("");
  };

  const createBeneficiary = () => {
    if (
      !username ||
      !password ||
      !name ||
      !birthdate ||
      !email ||
      !address ||
      !contact ||
      !nacionality
    ) {
      alert("Empty fields!");
      return;
    }

    for (var user of users) {
      if (user.username === username) {
        alert("Username already exists!");
        return;
      }
    }

    const newBenef = {
      id: uuidv4(),
      username: username,
      password: password,
      name: nameBenef,
      birthdate: birthdate,
      email: email,
      address: address,
      contact: contact,
      nacionality: nacionality,
    };

    handleClose();
  };

  const handleClose = () => {
    setShow(false);
    setUsername("");
    setPassword("");
    setName("");
    setBirthdate("");
    setEmail("");
    setAddress("");
    setContact("");
    setNacionality("");
    setNacionalityDropdownTitle("Select a nacionality");
  };
  const handleShow = () => setShow(true);

  return (
    <div className="addEvent_page">
      <Form>
        <Form.Group as={Row}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Title
          </Form.Label>
          <Col sm="10">
            <Form.Control
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row} style={{ marginTop: "10px" }}>
          <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
            Description
          </Form.Label>
          <Col sm="10">
            <Form.Control
              as="textarea"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Col>
        </Form.Group>

        <Form.Group as={Row}>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Target Reason
            </Form.Label>
            <Col sm="10">
              <Form.Control
                value={targetReason}
                onChange={(e) => setTargetReason(e.target.value)}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Target Amount
            </Form.Label>
            <Col sm="10" style={{ display: "flex" }}>
              <Form.Control
                value={targetAmount}
                onChange={(e) => setTargetAmount(e.target.value)}
              />
              <InputGroup.Text style={{ width: "8%" }}>â‚¬</InputGroup.Text>
            </Col>
          </Form.Group>
        </Form.Group>

        <DropdownButton title={benefDropdownTitle}>
          {benefs.length === 0 ? (
            <Dropdown.Item onClick={() => handleShow()}>
              Add Beneficiary
            </Dropdown.Item>
          ) : (
            <>
              {benefs.map((benef) => (
                <Dropdown.Item
                  key={benef.id}
                  as="button"
                  onClick={(e) => {
                    e.preventDefault();
                    setBenefDropdownTitle(benef.name);
                    setBenefId(benef.id);
                  }}
                >
                  {benef}
                </Dropdown.Item>
              ))}
              <Dropdown.Divider />
              <Dropdown.Item onClick={() => handleShow()}>
                Add Beneficiary
              </Dropdown.Item>
            </>
          )}
        </DropdownButton>

        <Modal
          show={show}
          onHide={handleClose}
          backdrop="static"
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Create Beneficiary</Modal.Title>
          </Modal.Header>

          <Modal.Body>
            <Form>
              <Form.Group as={Row}>
                <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                  Username
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row}>
                <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                  Password
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row}>
                <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                  Name
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="text"
                    value={nameBenef}
                    onChange={(e) => setNameBenef(e.target.value)}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row}>
                <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                  Email
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row}>
                <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                  Address
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="text"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                  />
                </Col>
              </Form.Group>

              <Form.Group as={Row}>
                <Form.Label column sm="2" style={{ fontWeight: "bold" }}>
                  Contact
                </Form.Label>
                <Col sm="10">
                  <Form.Control
                    type="number"
                    value={contact}
                    onChange={(e) => setContact(e.target.value)}
                  />
                </Col>
              </Form.Group>

              <DropdownButton title={nacionalityDropdownTitle}>
                {nacionalities.map((nac) => (
                  <Dropdown.Item
                    key={nac}
                    as="button"
                    onClick={(e) => {
                      e.preventDefault();
                      setNacionalityDropdownTitle(nac);
                      setNacionality(nac);
                    }}
                  >
                    {nac}
                  </Dropdown.Item>
                ))}
                )}
              </DropdownButton>
            </Form>
          </Modal.Body>

          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={() => createMis()}>
              Create
            </Button>
          </Modal.Footer>
        </Modal>

        <Form.Group as={Row}>
          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              Begin Date
            </Form.Label>
            <Col sm="10">
              <DatePicker
                todayButton="Today"
                dateFormat="dd/MM/yyyy"
                selected={beginDate}
                onChange={(date) => setBeginDate(date)}
                selectsStart
                startDate={beginDate}
                endDate={endDate}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Col} style={{ padding: "0px" }}>
            <Form.Label column sm="10" style={{ fontWeight: "bold" }}>
              End Date
            </Form.Label>
            <Col sm="10">
              <DatePicker
                todayButton="Today"
                dateFormat="dd/MM/yyyy"
                selected={endDate}
                onChange={(date) => setEndDate(date)}
                selectsEnd
                startDate={beginDate}
                endDate={endDate}
                minDate={beginDate}
              />
            </Col>
          </Form.Group>
        </Form.Group>
      </Form>

      <>
        <div>
          <Link to="/mis/events" className="btn btn-primary cancel">
            Cancel
          </Link>
          <div>
            <Link
              to="/mis/events"
              className="btn btn-primary add"
              onClick={addEvent}
            >
              Add
            </Link>
          </div>
        </div>
      </>
    </div>
  );
};

export default AddEvent;
