import { useState, useEffect } from "react";
import axios from "axios";

import Card from "react-bootstrap/Card";

const Order = ({ order }) => {
  const [event, setEvent] = useState([]);
  const [show, setShow] = useState(false);
  //Invoice Modal
  const [orderDescription, setOrderDescription] = useState("");
  const [orderDate, setOrderDate] = useState(Date());
  useEffect(() => {
    const getEventById = async () => {
      const event = await fetchEventById();
      setEvent(event);
    };
    getEventById();
  }, []);

  const fetchEventById = async () => {
    const eventId = { eventId: order.eventId };
    const response = await axios.post(
      "http://localhost:8080/get_EVENT_by_Id",
      eventId
    );
    return response.data;
  };

  const handleClose = () => {
    setShow(false);
    setOrderDescription("");
    setOrderDate(new Date());
  };
  const handleShow = () => setShow(true);

  const createOrder = async (event) => {
    // if (!description || !orderDate) {
    //   alert("Empty fields!");
    //   return;
    // }
    // console.log(event);
    // const newOrder = {
    //   id: uuidv4(),
    //   // oldId: event.id,
    //   description: orderDescription,
    //   date:
    //     new Date(orderDate).getDate() +
    //     "/" +
    //     (new Date(orderDate).getMonth() + 1) +
    //     "/" +
    //     new Date(orderDate).getFullYear(),
    //   misId: user.id,
    //   supplCoId: event.supplCoId,
    //   eventId: event.id,
    // };
    // try {
    //   await axios.post("http://localhost:8080/create_ORDER", newOrder);
    //   handleClose();
    // } catch (e) {
    //   console.log(e);
    // }
  };

  return (
    <div className="supplco_invoiceDetails">
      <div className="invoice">
        <Card>
          {event[0] ? (
            <Card.Header className="cardTitle">{event[0].name}</Card.Header>
          ) : (
            ""
          )}
          <Card.Body>
            <Card.Text>{order.description}</Card.Text>
            <Card.Text>{order.date}</Card.Text>
          </Card.Body>

          <Button
            variant="btn btn-dark btn-sm"
            onClick={() => handleShow()}
            style={{ float: "right", marginTop: "20px" }}
          >
            Create Invoice
          </Button>
        </Card>
      </div>
    </div>
  );
};

export default Order;
